import React, {useState} from 'react';
import GetItems from './getItems';
import TodoForm from './TodoForm';

function getItemsGET(){
  fetch('http://localhost:3000/api/item/all')
        .then(response => response.json())
        .then(data => {
          return data;
          /*console.log(data);
          console.log(data.length);
          for (var i = 0; i < data.length; i++) {
            console.log(data[i].id); //por ahora solo guardemos id y name
            console.log(data[i].name);
            console.log(data[i].checked);
            console.log(data[i].iconid);
            console.log(data[i].folderid);
            console.log(data[i].userid);
          } */
        }).catch(error => console.log(error));
}

function TodoList() {
    const [items, setItems] = useState([]);

    const addItem = item => {
        if (!item.text || /^\s*$/.test(item.text)) {
          return;
        }

        /* const newTodos = [todo, ...todos];

        // setTodos(newTodos);
         console.log(...todos);
        */

        //POST
        var nombre = item.text;
        fetch('http://localhost:3000/api/item/save', {
            method: 'POST',
            headers: { "Content-Type": "application/json" },
            body: JSON.stringify({ name: nombre})
          }).then(() => {
            //refresh list
            setItems(getItemsGET());
            console.log('new item added');
          }).catch(error => console.log(error));
    };

    return (
        <div>
            <GetItems items={items} />
            <br/> 
            <TodoForm onSubmit={addItem}/>
        </div>
    );
}

export default TodoList;
