import React, {useState} from 'react';
import {RiCloseCircleLine} from 'react-icons/ri';
import {TiEdit} from 'react-icons/ti';


function Todo(items, checkItem) {
    const[edit, setEdit]= useState({
        id:null,
        name: ''
    })
  return items.map((item) => (
    <div className={item.checked ? 'todo-row-checked' : 'todo-row'} key={item.id}>
        {item.checked ? 
                <input id={item.id} defaultChecked type="checkbox" onClick={()=> checkItem(item.id)} />  : 
                <input id={item.id} type="checkbox"  onClick={()=> checkItem(item.id)} />  

        }
        <div className='text-item'>{item.name}</div>
        <div className='icons'> 
        <RiCloseCircleLine /> 
        <TiEdit />
        </div>


    </div>


  ));
}

export default Todo;
