import React, { Component } from 'react';
import {RiCloseCircleLine} from 'react-icons/ri';
import {TiEdit} from 'react-icons/ti';

class GetItems extends Component {
  constructor(props){
    super(props);

    this.state = {
      reload: Boolean,
      items: [],
      input: String,
    }
  }

  getItemsGET(){
    fetch('http://localhost:3000/api/item/all')
      .then(response => response.json())
      .then(data => {
        this.setState({items: data});
        /*
        console.log(data);
        console.log(data.length);
        for (var i = 0; i < data.length; i++) {
          console.log(data[i].id); //por ahora solo guardemos id y name
          console.log(data[i].name);
          console.log(data[i].checked);
          console.log(data[i].iconid);
          console.log(data[i].folderid);
          console.log(data[i].userid);
        } */
      }).catch(error => console.log(error));
  }

  componentDidMount() {
     this.getItemsGET();
  }

  handleSubmit = e => { //adapt this
    e.preventDefault();

    props.onSubmit({
        text:input,
    });

    setInput('');
  };

  setInput(texto){
    this.setState({input: texto})
  }

  handleChange = e => { //adapt this
    setInput(e.target.value);
  };

  render() {
    const { items} = this.state;
    return (<div>
        List of posts
        {
              items.length ?
              items.map(item => <div key={item.id}>
                {item.checked ? 
                <input id={item.id} defaultChecked type="checkbox"  />  : 
                <input id={item.id} type="checkbox"  />  
                }
                {item.name}
                <a href=""> Edit</a>
                </div>) : null 
        }
              <div className='icons'> 
              <RiCloseCircleLine /> 
              <TiEdit />
              </div>  
    </div>);
  }
}

export default GetItems;
